import React, {Component} from 'react';
import { Card, Header } from 'semantic-ui-react';
import factory from '../ethereum/factory';


class App extends Component{


    constructor(props){

        super(prope)
        this.state={
            item:{}
        }
    } 

    static async getInitialProps(){
        const Campaign= await factory.methods.getCampaignsDeployed().call();
        return {Campaign};
    }


    render(){
    return (<div>
     {
         this.state.item= this.props.Campaign.map(address=> 
            {return {
                header: address,
                description: <h1>view Campaign</h1>,
                fluid: true
            };
            })
     }
     <Card.Group items={this.state.item}/>

    </div>);
    
    }
    
}

export default App;
